mapper.insert:
insert into board(
	orders_no,
	board_content
) values (
	#{ordersNo},
	#{boardContent}
)

mapper.select:
select count(*)
from board
where orders_no = #{ordersNo}

select b.orders_no ordersNo, b.board_content boardContent, b.update_date updateDate from board b inner join orders o on o.orders_no = b.orders_no
o.goods_no = #{goodsNo}
order by b.orders_no desc
