mapper.update:
UPDATE goods
SET goods_state = #{goodsState}
WHERE goods_no = #{goodsNo}

UPDATE goods
	SET
		goods_title = #{goodsTitle},
		goods_memo = #{goodsMemo},
		goods_price = #{goodsPrice}
WHERE goods_no = #{goodsNo}

mapper.select:
select g.goods_no goodsNo, g.goods_title goodsTitle, g.goods_memo goodsMemo, g.goods_state goodsState, c.category_no categoryNo, c.category_title categoryTitle, gf.goods_file_name goodsFileName, gf.goods_file_ext goodsFileExt from goods g inner join goods_category gc on g.goods_no = gc.goods_no inner join category c on gc.category_no = c.category_no left join (select goods_no, goods_file_name, goods_file_ext, row_number() over (partition by goods_no order by goods_file_name) firstFile from goods_file ) gf on g.goods_no = gf.goods_no and gf.firstFile = 1
c.category_no = #{categoryNo}
and g.goods_title like concat('%',#{searchTitle},'%')
order by goodsNo desc limit #{beginRow},#{rowPerPage}
